import { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { Context } from "../context/Context";
import axios from "axios";
import "../styles/topbar.css";

export default function Topbar() {

  const {user, dispatch} = useContext(Context);
  const [file, setFile] = useState(null);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogout = () => {
    dispatch({type: "LOGOUT"})
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    const updatedUser = {
      userId: user._id,
      username,
      email,
      password
    }
    if(file){
      const data = new FormData();
      const filename = Date.now() + file.name;
      data.append("name", filename);
      data.append("file", file);
      updatedUser.profilePic = filename;
      try{
        await axios.put("/upload", data);
      }catch(err){}
    }
    try{
      const res = await axios.post("/users", updatedUser);
      window.location.replace("/post/" + res.data._id);
    }catch(err){}
  }

  return (
    <div className="topbar">
      <div className="top-left">
        <h1 className="top-logo"><Link to="/">HOME</Link> </h1>
      </div>
      <div className="top-center">
        <ul className="topbar-list">
          <li className="list-item">
            <Link to="/about">ABOUT</Link>  
          </li>
          <li className="list-item">  
            <Link to="/contact">CONTACT</Link>
          </li>
          <li className="list-item">
            <Link to="/write">WRITE</Link>
          </li>
          <li className="list-item logout-link" onClick={handleLogout}>{user && "LOGOUT"}</li>
        </ul>
      </div>
      <div className="top-right">
        <div className="img-search">
          {
            user ? (
              <Link to="/settings">
                <img className="top-img" src={user.profilePic ? user.profilePic : "https://static.vecteezy.com/system/resources/previews/002/534/006/original/social-media-chatting-online-blank-profile-picture-head-and-body-icon-people-standing-icon-grey-background-free-vector.jpg"} alt="" />
              </Link>
            ) : (
              <ul className="topbar-list">
                <li className="list-item"><Link to="/login">LOGIN</Link></li>
                <li className="list-item"><Link to="/register">REGISTER</Link></li>
              </ul>
            )
          }
          <i className="top-search fas fa-search"></i>
        </div>
      </div>
    </div>
  )
}
