import { useState, useEffect } from 'react';
import { useLocation } from 'react-router';
import { Link } from 'react-router-dom';
import { Context } from '../context/Context';
import axios from 'axios';
import "../styles/show.css";

export default function ShowPost() {
  const location = useLocation();
  const path = location.pathname.split("/")[2];
  const [post, setPost] = useState({});
  const {user} = useContext(Context)
  const PUBLIC_FOLDER = "http://localhost:5000/images/";

  const deletePost = async () => {
    try{
      const res = await axios.delete("/post/" + path);
      window.location.replace("/home/");
    }catch(err){
      console.log(err)
    }
  }

  useEffect(() => {
    const getPost = async () => {
      const res = await axios.get("/posts/" + path);
      setPost(res.data.post)
    };
    getPost();
  }, [path])

  return (
    <div className="show-post">
      <div className="show-wrapper">
        {post.photo && 
          <img
          className="show-img" 
          src={PUBLIC_FOLDER + post.photo} 
          alt="" 
          />
        }
        <h1 className="show-title">
          {post.title}
          {post.username === user.username && 
            <div className="show-edit-delete">
              <i className="show-edit far fa-edit"></i>
              <i className="show-delete far fa-trash-alt" onClick={deletePost}></i>
            </div>
          }
        </h1>
        <div className="show-info">
          <span className="show-info-author">
            <Link to={`/?user=${post.username}`}>
              Author: <b>{post.username}</b>
            </Link>
          </span>
          <span className="show-info-date">{new Date(post.createdAt).toDateString()}</span>
        </div>
        <div className="show-description">
          {post.description}
        </div>
      </div>
    </div>
  )
}
