import { useContext, useState } from 'react';
import { Context } from '../context/Context';
import axios from 'axios';
import '../styles/write.css';

export default function Write() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [file, setFile] = useState("");
  const {user} = useContext(Context);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newPost = {
      username: user.username,
      title,
      description
    }
    if(file){
      const data = new FormData();
      const filename = Date.now() + file.name;
      data.append("name", filename);
      data.append("file", file);
      newPost.photo = filename;
      try{
        await axios.post("/upload", data);
      }catch(err){
        console.log(err)
      }
    }
    try{
      const res = await axios.post("/posts", newPost);
      window.location.replace("/post/" + res.data._id);
    }catch(err){
      console.log(err)
    }
  } 

  return (
    <div className="write">
      {file && 
        <img className="write-img" src={URL.createObjectURL(file)} alt="new article" />
      }
      <form className="write-form" onSubmit={handleSubmit}>
        <div className="write-form-group">
          <label htmlFor="file-input">
            <i className="write-icon fas fa-plus"></i>
          </label>
          <input type="file" id="file-input" hidden="true" onChange={e => setFile(e.target.files[0])} />
          <input type="text" placeholder="Title" className="write-input" autoFocus="true" />
        </div>
        <div className="write-form-group">
          <textarea placeholder="Tell your story..." type="text" className="write-input write-text"></textarea>
        </div>
        <button className="write-submit" type="submit">Publish</button>
      </form>
    </div>
  )
}
