import axios from 'axios';
import '../styles/write.css';

export default function Write() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [file, setFile] = useState("");
  const {user} = useContext(Context);

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post("/posts");
  } 

  return (
    <div className="write">
      <img className="write-img" src="https://images.pexels.com/photos/267392/pexels-photo-267392.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" alt="new article" />
      <form className="write-form" onSubmit={handleSubmit}>
        <div className="write-form-group">
          <label htmlFor="file-input">
            <i className="write-icon fas fa-plus"></i>
          </label>
          <input type="file" id="file-input" hidden="true" />
          <input type="text" placeholder="Title" className="write-input" autoFocus="true" />
        </div>
        <div className="write-form-group">
          <textarea placeholder="Tell your story..." type="text" className="write-input write-text"></textarea>
        </div>
        <button className="write-submit" type="submit">Publish</button>
      </form>
    </div>
  )
}
