import React from 'react';
import "../styles/posts.css";
import Posts from './Posts';

export default function Post({post}) {
  return (
    <div className="post">
      <img className="post-img" src="https://images.pexels.com/photos/261579/pexels-photo-261579.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" alt="work & studies" />
      <div className="post-info">
        <div className="post-categories">
          <span className="post-category">Music</span>
          <span className="post-category">Life</span>
        </div>
        <span className="post-title">{post.title}</span>
        <hr />
        <span className="post-date">{new Date(post.createdAt).toDateString()}</span>
      </div>
      <p className="post-description">{post.description}</p>
    </div>
  )
}
